fn main() {
    let floor = get_final_floor(INPUT);

    println!("final floor is {floor}!");

    if let Some(earliest_underground) = get_earliest_underground(INPUT) {
        println!("earliest underground is {earliest_underground}!");    
    } else {
        println!("We never went underground!");
    }
    
}

fn get_earliest_underground(input: &str) -> Option<usize> {
    let mut floor = 0;

    for (i, x) in input.chars().enumerate() {
        floor += match x {
            '(' => 1,
            ')' => -1,
            _ => 0,
        };

        if floor < 0 {
            return Some(i + 1)
        }
    }

    None
}

fn get_final_floor(input: &str) -> i32 {
    input.chars().fold(0, |total, char| {
        total
            + match char {
                '(' => 1,
                ')' => -1,
                _ => 0,
            }
    })
}
